import {useEffect, useState} from 'react'

import styles from '../components/css/Character.module.css'

function Character({id}){

    const [char, setChar] = useState([])
    const [planetOrigin, setPlanetOrigin] = useState([])
    const [episodes, setEpisodes] = useState([])

    //character
    useEffect(() => {
        setTimeout(
            () => {
                fetch(`https://rickandmortyapi.com/api/character/${id}` , {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                })
                    .then((resp) => resp.json())
                    .then((data) => {
                        console.log(data)
                        setChar(data)
                        setPlanetOrigin(data.origin.name)
                        setEpisodes(data.episode)
                        console.log(episodes)

                    })
                    .catch((err) => console.log(err))
            }, 1000)
    },[])
    //episodes
    useEffect(() => {
        setTimeout(
            () => {
                fetch(`https://rickandmortyapi.com/api/character/${id}` , {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                })
                    .then((resp) => resp.json())
                    .then((data) => {
                        console.log(data)
                        setChar(data)
                        setPlanetOrigin(data.origin.name)
                        setEpisodes(data.episode[1].name)

                    })
                    .catch((err) => console.log(err))
            }, 1000)
    },[])




    return (
        <div className={styles.project_card}>
            <div>
                <p>Name: <span>{char.name}</span></p>
                <img src={char.image} alt={`${char.name}_img`}/>
            </div>
            <p>Status: <span>{char.status}</span></p>
            <p>Planeta de origem: {planetOrigin}</p>
            <p>episodes:</p>
            <p>{episodes}</p>
        </div>
    )
}

export default Character
