import {useEffect, useState} from 'react'

import styles from '../components/css/Character.module.css'

function Character({id}){

    const [char, setChar] = useState([])
    const [planetOrigin, setPlanetOrigin] = useState([])
    const [episodeOne, setEpisodeOne] = useState([])
    const episodes = [];
    const test = Array.from(episodeOne)

    //character
    useEffect(() => {
                fetch(`https://rickandmortyapi.com/api/character/${id}` , {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                })
                    .then((resp) => resp.json())
                    .then((data) => {
                        console.log(data)
                        setChar(data)
                        setPlanetOrigin(data.origin.name)

                        //Episodes
                        data.episode.length = 3
                        for (let i = 0; i < data.episode.length; i++){
                            episodes.push(data.episode[i])
                            episodes.slice(0,2);

                            fetch(episodes[i] , {
                                method: 'GET',
                                headers: {
                                    'Content-Type': 'application/json',
                                },
                            })
                                .then((resp) => resp.json())
                                .then(data => {
                                    setEpisodeOne(data)
                                })
                                .catch((err) => console.log(err))

                        }
                    })
                    .catch((err) => console.log(err))
    },[id])
    console.log(episodeOne)
    return (
        <div className={styles.project_card}>
        <div>
            <p>Name: <span>{char.name}</span></p>
            <img src={char.image} alt={`${char.name}_img`}/>
        </div>
        <p>Status: <span>{char.status}</span></p>
        <p>Planeta de origem: {planetOrigin}</p>
        <p>episodes:</p>
        {episodeOne[2].name}
        </div>
    )
}
export default Character
